mapper.select:
select v.student_instructor_uname as studentInstructorUname, v.student_class_name as studentClassName, v.vacate_type as vacateType, v.vacate_number as vacateNumber, n.notice_teacher_uname as noticeTeacherUname, n.vacate_start_time as vacateStartTime, n.vacate_end_time as vacateEndTime, n.notice_is_read as noticeIsRead, n.notice_create_time as noticeCreateTime, n.vacate_notice_id as vacateNoticeId, n.vacate_student_uname as vacateStudentUname from e_vacate_notice as n left join e_student_vacate as v on n.notice__reference_id = v.student_vacate_id where notice_is_vaild = '1' and notice_teacher_uid = #{userId,jdbcType=VARCHAR}
and vacate_student_uname like CONCAT('%',#{userName,jdbcType=VARCHAR},'%')
order by notice_create_time,notice_is_read
